const getGroup=e=>{switch(e){case"Media":case"Art & Culture":return groups[0];case"Politics":case"Civics":case"Democracy":return groups[1];case"Community":case"Intercultural":case"Service":case"Conflict resolution":case"Anti-Hate":case"Polarization":case"Religion":case"Interfaith":return groups[2];case"Sharing Economy":case"Business":case"International Relief":return groups[3];case"Technology":case"Digital Equity":case"Interdependence":return groups[4];case"Education":case"Research":return groups[5]}},getColor=e=>{switch(getGroup(e)){case"communications":return colors.find(e=>"red"===e.id);case"civics":return colors.find(e=>"yellow"===e.id);case"community":return colors.find(e=>"blue"===e.id);case"economy":return colors.find(e=>"orange"===e.id);case"technology":return colors.find(e=>"teal"===e.id);case"education":return colors.find(e=>"pistachio"===e.id)}};